import Vue from 'vue'
import Router from 'vue-router'
import Meta from 'vue-meta'

// import Home from './views/home'
import Login from './views/login2'
import NoteFormEdit from './components/note'
import NoteForm2 from './components/create-note-form'
import NoteForm3 from './components/AddNote'
import noteList from './components/noteList'
import Register from './views/register.vue';
import Login2 from './views/login.vue';
import './style.css'

Vue.use(Router)
Vue.use(Meta)
export default new Router({
  mode: 'history',
  routes: [
    // {
    //   name: 'Home',
    //   path: '/home',
    //   component: Home,
    // },
    {
      name: 'Login',
      path: '/login',
      component: Login2,
      meta: {hideNavbar: true}
    },
    {
      name: 'Register',
      path: '/register',
      component: Register,
      meta: {hideNavbar: true}
    },
    {
      name: 'Home',
      path: '/home',
      component: noteList,
    },
    {
      name: 'add-note',
      path: '/add',
      component: NoteForm3,
    },
    {
      name: 'note-details',
      path: '/note/:id',
      component: NoteFormEdit,
    },
  ]

})
Router.beforeEach((to, from, next) => {
  const publicPages = ['/login', '/register'];
  const authRequired = !publicPages.includes(to.path);
  const loggedIn = localStorage.getItem('user');

  // trying to access a restricted page + not logged in
  // redirect to login page
  if (authRequired && !loggedIn) {
    next('/login');
  } else {
    next();
  }
});
