import Vue from 'vue'
import Router from 'vue-router'
import Meta from 'vue-meta'

// import Home from './views/home'
import Login from './views/login2'
import NoteFormEdit from './components/note'
import NoteForm2 from './components/create-note-form'
import noteAdd from './components/AddNote'
import noteList from './components/noteList'
import Labels from './components/labelList'
import Register from './views/register.vue';
import Login2 from './views/login.vue';
import './style.css'

Vue.use(Router)
Vue.use(Meta)
export default new Router({
  mode: 'history',
  routes: [
    // {
    //   name: 'Home',
    //   path: '/home',
    //   component: Home,
    // },
    {
      path:'/',
      redirect:'/login'
    },
    {
      name: 'Login',
      path: '/login',
      component: Login2,
      meta: {hideNavbar: true}
    },
    {
      name: 'Register',
      path: '/register',
      component: Register,
      meta: {hideNavbar: true}
    },
    {
      name: 'Home',
      path: '/home',
      component: noteList,
    },
    {
      name: 'Labels',
      path: '/label',
      component: Labels,
    },
    {
      name: 'add-note',
      path: '/add',
      component: noteAdd,
    },
    {
      name: 'note-details',
      path: '/note/:id',
      component: NoteFormEdit,
    },
  ]

})
